<?php
namespace Shiptor\Bundle\FiasBundle\Repository;

use Doctrine\Common\Collections\Criteria;
use Shiptor\Bundle\FiasBundle\Entity\UpdateList;

/**
 * UpdateListRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class UpdateListRepository extends \Doctrine\ORM\EntityRepository
{
    /**
     * @param $version
     *
     * @return \Doctrine\ORM\QueryBuilder
     */
    public function getUpdateList($version)
    {
        $qb = $this->createQueryBuilder('ul');

        if ($version) {
            $qb
                ->andWhere('ul.versionId = :versionId')
                ->setParameter('versionId',$version);
        } else {
            $qb->where('ul.updatedAt IS NULL');
        }

        return $qb->orderBy('ul.versionId', 'ASC');
    }

    /**
     * @return UpdateList
     */
    public function findLast()
    {
        $versions = $this->findBy([], ['versionId' => Criteria::DESC], 1);

        return count($versions) ? $versions[0] : null;
    }
}
